import React, { useState } from "react";
import { useForm } from "react-hook-form";
import Router from "next/router";
import styles from "../styles/Login.module.css";
import ErrorMessage from "../components/ErrorMessage";

const Login = () => {
  const { register, handleSubmit, errors } = useForm();

  const [showError, setShowError] = useState(false);

  function handleLoginClick(data) {
    const user = {
      username: data.user.username,
      password: data.user.password
    };
    fetch("http://localhost:3000/users/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
      },
      body: JSON.stringify(user)
    })
      .then(res => res.json())
      .then(res => {
        if (res.token) {
          localStorage.setItem("token", res.token);
          Router.push("/");
        } else setShowError(true);
      })
      .catch(error => console.log(error));
  }

  return (
    <form
      className={styles.loginContainer}
      onSubmit={handleSubmit(handleLoginClick)}
    >
      <h1 className={styles.loginTitle}>Login</h1>
      <input
        className={styles.loginInput}
        placeholder="Username"
        name="user.username"
        ref={register({
          required: true
        })}
      />
      <div className={styles.mailErrorMessage}>
        {errors["user.username"] && errors["user.username"].message}
      </div>
      <input
        className={styles.loginInput}
        placeholder="Password"
        name="user.password"
        type="password"
        ref={register({
          required: true
        })}
      />
      <div className={styles.rememberMeContainer}>
        <input
          type="checkbox"
          id="user.rememberMe"
          name="user.rememberMe"
          ref={register}
        />
        <label className={styles.rememberMeText} htmlFor="user.rememberMe">
          Remember me
        </label>
      </div>
      <button className={styles.loginButton} type="submit">
        Login
      </button>
      {showError ? (
        <ErrorMessage message={"Wrong username or password"} />
      ) : null}
    </form>
  );
};

export default Login;
