import { useState } from "react";
import Router from "next/router";

const Index = () => {
  const [title, setTitle] = useState("");
  const [category, setCategory] = useState("Development");
  const [readTime, setReadTime] = useState("");
  const [part1, setPart1] = useState("");
  const [part1Type, setPart1Type] = useState("Bold");
  const [part2, setPart2] = useState("");
  const [part2Type, setPart2Type] = useState("Bold");
  const [part3, setPart3] = useState("");
  const [part3Type, setPart3Type] = useState("Bold");

  const onSubmit = () => {
    const post = {
      title: title,
      category: category,
      readTime: readTime,
      parts: [
        {
          type: part1Type,
          ordinal: 1,
          content: part1
        },
        {
          type: part2Type,
          ordinal: 2,
          content: part2
        },
        {
          type: part3Type,
          ordinal: 3,
          content: part3
        }
      ]
    };

    fetch("http://localhost:3000/posts", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json",
        "Authorization": "JWT " + localStorage.getItem("token")
      },
      body: JSON.stringify(post)
    })
      .then(res => res.json())
      .then(res => console.log(res));
  };

  const handleChange = setterFunction => event => {
    setterFunction(event.target.value);
  };

  const onLogout = () => {
    localStorage.removeItem("token");
    Router.push('/login');
  }

  const containerStyle = {
    padding: "20px",
    margin: "20px",
    border: "1px solid #ccc"
  };

  const buttonStyle = {
    margin: "0px 0px 8px 20px",
  }

  return (
    <>
      <div style={containerStyle}>
        <span>Title</span>
        <input value={title} onChange={handleChange(setTitle)} /> <br /><br />
        <span>Category</span>
        <select value={category} onChange={handleChange(setCategory)}>
          <option value="Development">Development</option>
          <option value="Design">Design</option>
          <option value="Marketing">Marketing</option>
          <option value="Sales">Sales</option>
        </select> <br /><br />
        <span>Read time</span>
        <input value={readTime} onChange={handleChange(setReadTime)} /> <br />
      </div>
      <div style={containerStyle}>
        <div>Part 1</div>
        <select value={part1Type} onChange={handleChange(setPart1Type)}>
          <option value="Bold">Bold</option>
          <option value="Subsection">Subsection</option>
          <option value="Quote">Quote</option>
        </select>
        <br />
        <textarea value={part1} onChange={handleChange(setPart1)} />
      </div>
      <div style={containerStyle}>
        <div>Part 2</div>
        <select value={part2Type} onChange={handleChange(setPart2Type)}>
          <option value="Bold">Bold</option>
          <option value="Subsection">Subsection</option>
          <option value="Quote">Quote</option>
        </select>
        <br />
        <textarea value={part2} onChange={handleChange(setPart2)} /> <br />
      </div>
      <div style={containerStyle}>
        <div>Part 3</div>
        <select value={part3Type} onChange={handleChange(setPart3Type)}>
          <option value="Bold">Bold</option>
          <option value="Subsection">Subsection</option>
          <option value="Quote">Quote</option>
        </select>
        <br />
        <textarea value={part3} onChange={handleChange(setPart3)} /> <br />
      </div>
      <button style={buttonStyle} onClick={onSubmit}>Submit</button> <br />
      <button style={buttonStyle} onClick={onLogout}>Logout</button>
    </>
  );
};

export default Index;
