import React, { useState } from "react";
import Router from 'next/router'
import { useForm } from "react-hook-form";
import queryString from "querystring";
import axios from "axios";
import styles from "../styles/Login.module.css";
import ErrorMessage from "../components/ErrorMessage";
const jwt = require("jsonwebtoken");
const publicKey = "";

const Login = () => {
  const { register, handleSubmit, errors } = useForm();

  const [showError, setShowError] = useState(false);

  function handleLoginClick(data) {
    const params = {
      client_secret: "489eedf0-1ad8-4ebd-bf60-426709339e66",
      username: data.username,
      password: data.password,
      grant_type: "password",
      client_id: "frontend"
    };

    axios({
      method: "post",
      url: "http://localhost:8080/auth/realms/govanj/protocol/openid-connect/token",
      data: queryString.stringify(params),
      config: {
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"
        }
      }
    }).then(res => {
      if (res.request.status !== 200){
        console.log("Not authorized");
        return;
      }
      const decoded = jwt.verify(res.data.access_token, publicKey);
      console.log(decoded);
      console.log(res.data.access_token);
      Router.push("/registration");
    })
      .catch(error => {
        console.error(error);
      });
  }

  return (
    <form
      className={styles.loginContainer}
      onSubmit={handleSubmit(handleLoginClick)}
    >
      <h1 className={styles.loginTitle}>Login</h1>
      <input
        className={styles.loginInput}
        placeholder="Username"
        name="username"
        ref={register({
          required: true
        })}
      />
      <div className={styles.mailErrorMessage}>
        {errors["username"] && errors["username"].message}
      </div>
      <input
        className={styles.loginInput}
        placeholder="Password"
        name="password"
        type="password"
        ref={register({
          required: true
        })}
      />
      <button className={styles.loginButton} type="submit">
        Login
      </button>
      {showError ? (
        <ErrorMessage message={"Wrong username or password"} />
      ) : null}
    </form>
  );
};

export default Login;
