import { useState } from "react";
import Router from "next/router";
import Link from "next/link";

const Mail = () => {
  const [title, setTitle] = useState("");
  const [to, setTo] = useState("");
  const [from, setFrom] = useState("");
  const [mailBody, setMailBody] = useState("");

  const onSubmit = () => {
    const post = {
      title: title,
      category: category,
      readTime: readTime,
      parts: [
        {
          type: part1Type,
          ordinal: 1,
          content: part1
        },
        {
          type: part2Type,
          ordinal: 2,
          content: part2
        },
        {
          type: part3Type,
          ordinal: 3,
          content: part3
        }
      ]
    };

    fetch("http://localhost:3000/posts", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json",
        "Authorization": "JWT " + localStorage.getItem("token")
      },
      body: JSON.stringify(post)
    })
      .then(res => res.json())
      .then(res => console.log(res));
  };

  const handleChange = setterFunction => event => {
    setterFunction(event.target.value);
  };

  const onLogout = () => {
    console.log("LOGOUT");
    Router.push('/login');
  }

  const containerStyle = {
    padding: "20px",
    margin: "20px",
    border: "1px solid #ccc"
  };

  const buttonStyle = {
    margin: "0px 0px 8px 20px",
  }

  return (
    <>
      <div style={containerStyle}>
        <span>Title</span>
        <input value={title} onChange={handleChange(setTitle)} /> <br /><br />
        <span>To</span>
        <input value={to} onChange={handleChange(setTo)} /> <br /><br />
        <span>From</span>
        <input value={from} onChange={handleChange(setFrom)} /> <br /><br />
        <span>Body</span>
        <textarea value={mailBody} onChange={handleChange(setMailBody)} /> <br /><br />
      </div>
      <button style={buttonStyle} onClick={onSubmit}>Submit</button> <br />
      <Link href="/login">
        <button style={buttonStyle} onClick={onLogout}>Logout</button>
      </Link>
    </>
  );
};

export default Mail;
