import React, { useState } from "react";
import { useForm } from "react-hook-form";
import styles from "../styles/Registration.module.css";
import ErrorMessage from "../components/ErrorMessage";

const Registration = () => {
  const { register, handleSubmit, errors } = useForm();
  const [passwordsNotMatching, setPasswordsNotMatching] = useState(false);

  function handleRegistrationClick(data) {
    console.log("Registration");
    console.log(data);
  }

  return (
    <form
      className={styles.registrationContainer}
      onSubmit={handleSubmit(handleRegistrationClick)}
    >
      <h1 className={styles.registrationTitle}>Register</h1>
      <input
        className={styles.registrationInput}
        placeholder="Full name"
        name="user.fullName"
        ref={register({
          required: true
        })}
      />
      <input
        className={styles.registrationInput}
        placeholder="Email"
        type="email"
        name="user.email"
        ref={register({
          required: true,
          pattern: {
            value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
            message: "This is not a valid email"
          }
        })}
      />
      <div className={styles.formError}>
        {errors["user.email"] && errors["user.email"].message}
      </div>
      <input
        className={styles.registrationInput}
        placeholder="Password"
        type="password"
        name="user.password"
        ref={register({
          required: true,
          minLength: {
            value: 8,
            message: "Password has to be 8 characters or longer"
          }
        })}
      />
      <div className={styles.formError}>
        {errors["user.password"] && errors["user.password"].message}
      </div>
      <input
        className={styles.registrationInput}
        placeholder="Confirm password"
        type="password"
        name="user.confirmPassword"
        ref={register({
          required: true
        })}
      />
      <button className={styles.registrationButton} type="submit">
        Register
      </button>
      {passwordsNotMatching ? (
        <ErrorMessage message={"Passwords are not matching"} />
      ) : null}
      {/* {showError ? <ErrorMessage message={"Something went wrong"} /> : null} */}
    </form>
  );
};

export default Registration;
