import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import queryString from "querystring";
import styles from "../styles/Login.module.css";
import ErrorMessage from "../components/ErrorMessage";
import axios from "axios";

const Login = () => {
  const { register, handleSubmit, errors } = useForm();

  const [showError, setShowError] = useState(false);

  function handleLoginClick(data) {
    const params = {
      client_secret: "489eedf0-1ad8-4ebd-bf60-426709339e66",
      username: data.user.username,
      password: data.user.password,
      grant_type: "password",
      client_id: "frontend"
    };

    axios({
      method: "post",
      url: "http://localhost:8080/auth/realms/govanj/protocol/openid-connect/token",
      data: queryString.stringify(params),
      config: {
        headers: { "Content-Type": "application/x-www-form-urlencoded" }
      }
    })
      .then(response => {
        console.log(response.data);
      })
      .catch(error => {
        console.error(error);
      });

    /* let body = new FormData();
    body.append("client_secret", "489eedf0-1ad8-4ebd-bf60-426709339e66");
    body.append("username", data.user.username);
    body.append("password", data.user.password);
    body.append("grant_type", "password");
    body.append("client_id", "frontend");
    fetch("http://localhost:8080/auth/realms/govanj/protocol/openid-connect/token", {
        method: "POST",
        headers: {
          "Accept": "application/x-www-form-urlencoded",
          "Content-Type": "application/x-www-form-urlencoded"
        },
        body: body
      }
    )
      .then(res => res.json())
      .then(res => console.log(res))
      .catch(error => console.log("error")); */
  }

  return (
    <form
      className={styles.loginContainer}
      onSubmit={handleSubmit(handleLoginClick)}
    >
      <h1 className={styles.loginTitle}>Login</h1>
      <input
        className={styles.loginInput}
        placeholder="Username"
        name="user.username"
        ref={register({
          required: true
        })}
      />
      <div className={styles.mailErrorMessage}>
        {errors["user.username"] && errors["user.username"].message}
      </div>
      <input
        className={styles.loginInput}
        placeholder="Password"
        name="user.password"
        type="password"
        ref={register({
          required: true
        })}
      />
      <button className={styles.loginButton} type="submit">
        Login
      </button>
      {showError ? (
        <ErrorMessage message={"Wrong username or password"} />
      ) : null}
    </form>
  );
};

export default Login;
