import { useState } from "react";
import Router from "next/router";
import styles from "../styles/Mail.module.css";

const Mail = () => {
  const [title, setTitle] = useState("");
  const [to, setTo] = useState("");
  const [from, setFrom] = useState("");
  const [mailBody, setMailBody] = useState("");

  const onSubmit = () => {
    const post = {
      title: title,
      category: category,
      readTime: readTime,
      parts: [
        {
          type: part1Type,
          ordinal: 1,
          content: part1
        },
        {
          type: part2Type,
          ordinal: 2,
          content: part2
        },
        {
          type: part3Type,
          ordinal: 3,
          content: part3
        }
      ]
    };

    fetch("http://localhost:3000/posts", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json",
        "Authorization": "JWT " + localStorage.getItem("token")
      },
      body: JSON.stringify(post)
    })
      .then(res => res.json())
      .then(res => console.log(res));
  };

  const handleChange = setterFunction => event => {
    setterFunction(event.target.value);
  };

  const onLogout = () => {
    Router.push("/login");
  }

  return (
    <>
      <div style={styles.containerStyle}>
        <span>Title</span>
        <input value={title} onChange={handleChange(setTitle)} /> <br /><br />
        <span>To</span>
        <input value={to} onChange={handleChange(setTo)} /> <br /><br />
        <span>From</span>
        <input value={from} onChange={handleChange(setFrom)} /> <br /><br />
        <span>Body</span>
        <textarea value={mailBody} onChange={handleChange(setMailBody)} /> <br /><br />
      </div>
      <button style={styles.buttonStyle} onClick={onSubmit}>Submit</button> <br />
      <button style={styles.buttonStyle} onClick={onLogout}>Logout</button>
    </>
  );
};

export default Mail;
